import React, { useEffect, useState } from 'react';
import { apiFetch } from '../services/api';
import { useAuth } from '../services/AuthContext';

function Dashboard() {
  const { logout } = useAuth();
  const [contacts, setContacts] = useState([]);
  const [search, setSearch] = useState('');
  const [form, setForm] = useState({ name: '', email: '', phone: '' });
  const [editingId, setEditingId] = useState(null);
  const [error, setError] = useState('');

  const fetchContacts = async () => {
    try {
      const res = search
        ? await apiFetch(`/contacts/search/${search}`)
        : await apiFetch('/contacts');
      setContacts(res);
    } catch {
      setError('Failed to fetch contacts');
    }
  };

  useEffect(() => {
    fetchContacts();
  }, [search]);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setError('');
    try {
      if (editingId) {
        await apiFetch(`/contacts/${editingId}`, { method: 'PUT', body: form });
      } else {
        await apiFetch('/contacts', { method: 'POST', body: form });
      }
      setForm({ name: '', email: '', phone: '' });
      setEditingId(null);
      fetchContacts();
    } catch {
      setError('Failed to save contact');
    }
  };

  const handleEdit = contact => {
    setForm({ name: contact.name, email: contact.email, phone: contact.phone });
    setEditingId(contact._id);
  };

  const handleDelete = async id => {
    if (!window.confirm('Delete this contact?')) return;
    try {
      await apiFetch(`/contacts/${id}`, { method: 'DELETE' });
      fetchContacts();
    } catch {
      setError('Failed to delete contact');
    }
  };

  return (
    <div className="container py-4">
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Contact Book</h2>
        <button className="btn btn-outline-danger" onClick={logout}>Logout</button>
      </div>
      <div className="row mb-4">
        <div className="col-md-6">
          <form onSubmit={handleSubmit} className="card card-body mb-3">
            <h5>{editingId ? 'Edit Contact' : 'Add Contact'}</h5>
            <div className="mb-2">
              <input name="name" className="form-control" placeholder="Name" value={form.name} onChange={handleChange} required />
            </div>
            <div className="mb-2">
              <input name="email" className="form-control" placeholder="Email" value={form.email} onChange={handleChange} required />
            </div>
            <div className="mb-2">
              <input name="phone" className="form-control" placeholder="Phone" value={form.phone} onChange={handleChange} required />
            </div>
            {error && <div className="alert alert-danger py-1">{error}</div>}
            <button className="btn btn-primary" type="submit">{editingId ? 'Update' : 'Add'}</button>
            {editingId && <button className="btn btn-secondary ms-2" type="button" onClick={() => { setEditingId(null); setForm({ name: '', email: '', phone: '' }); }}>Cancel</button>}
          </form>
        </div>
        <div className="col-md-6">
          <input className="form-control mb-3" placeholder="Search contacts..." value={search} onChange={e => setSearch(e.target.value)} />
          <div className="list-group">
            {contacts.map(contact => (
              <div key={contact._id} className="list-group-item d-flex justify-content-between align-items-center">
                <div>
                  <div><strong>{contact.name}</strong></div>
                  <div className="text-muted small">{contact.email} | {contact.phone}</div>
                </div>
                <div>
                  <button className="btn btn-sm btn-outline-primary me-2" onClick={() => handleEdit(contact)}>Edit</button>
                  <button className="btn btn-sm btn-outline-danger" onClick={() => handleDelete(contact._id)}>Delete</button>
                </div>
              </div>
            ))}
            {contacts.length === 0 && <div className="text-center text-muted py-3">No contacts found.</div>}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
